import React, { useRef, useState,useEffect } from 'react';

// Define the AudioPlayer functional component
const AudioPlayer = ({audiofile}) => {
  // Create a ref for the audio element
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  // Function to toggle play/pause
  const togglePlayPause = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.playbackRate = 1;
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  // Effect to handle the end of the audio playback
  useEffect(() => {
    const handleEnded = () => {
      setIsPlaying(false);
    };

    const audioElement = audioRef.current;
    if (audioElement) {
      audioElement.addEventListener('ended', handleEnded);
    }

    // Cleanup event listener on component unmount
    return () => {
      if (audioElement) {
        audioElement.removeEventListener('ended', handleEnded);
      }
    };
  }, []);

  return (
    <div style={{display:'flex', justifyContent:'center'}}>
      <button
       onClick={togglePlayPause}
       style={{backgroundColor:'orange', padding:'10px', border:'none', borderRadius:'5px', outline:'none', fontWeight:'bold', textTransform:'uppercase', boxShadow:'0 2px 5px rgba(0,0,0,0.2)', transition:'0.3s', marginTop:'10px', border:'1px solid r'}}
       >
        {isPlaying ? 'PAUSE' : 'PLAY'}
      </button>
      <audio ref={audioRef} src={audiofile} />
    </div>
  );
};


export default AudioPlayer;
