import React, { useState } from 'react';
import './style.css';

const HighlightedSentence = ({ sentence, matchedPos }) => {
  const [level, setLevel] = useState(2);
  const [anchorEl, setAnchorEl] = useState(null);

  const handleLevelDecrease = () => {
    setLevel(prevLevel => Math.max(1, prevLevel - 1));
  };

  const handleLevelIncrease = () => {
    setLevel(prevLevel => Math.min(5, prevLevel + 1));
  };

  const handleOpenMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseMenu = () => {
    setAnchorEl(null);
  };

  const handleMenuItemClick = (level) => {
    setLevel(level);
    handleCloseMenu();
  };

  return (
    <div>
      <div className="highlighted-sentence">
        {/* Render the highlighted sentence here based on the matchedPos */}
        {sentence}
      </div>
      <div className="level-controls">
        <button onClick={handleLevelDecrease}>Left</button>
        <button onClick={handleOpenMenu}>Level {level}</button>
        <button onClick={handleLevelIncrease}>Right</button>
      </div>
      {anchorEl && (
        <div className="menu" style={{ position: 'absolute', top: anchorEl.getBoundingClientRect().bottom + 'px', left: anchorEl.getBoundingClientRect().left + 'px' }}>
          {[1, 2, 3, 4, 5].map(l => (
            <div key={l} className="menu-item" onClick={() => handleMenuItemClick(l)}>
              Level {l}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HighlightedSentence;
