import React, { useState } from 'react';
import './style.css';

const HighlightedSentence = ({ sentence, matchedPos }) => {
  const [level, setLevel] = useState(2);
  const [anchorEl, setAnchorEl] = useState(null);

  const handleLevelDecrease = () => {
    setLevel(prevLevel => Math.max(1, prevLevel - 1));
  };

  const handleLevelIncrease = () => {
    setLevel(prevLevel => Math.min(5, prevLevel + 1));
  };

  const handleOpenMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseMenu = () => {
    setAnchorEl(null);
  };

  const handleMenuItemClick = (level) => {
    setLevel(level);
    handleCloseMenu();
  };

  return (
    <div>
      <div className="level-controls" style={{ marginTop:'10px', marginLeft:'20px', marginRight:'20px',display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <button onClick={handleLevelDecrease}>Left</button>
        <button onClick={handleOpenMenu}>Level {level}</button>
        <button onClick={handleLevelIncrease}>Right</button>
      </div>
      {anchorEl && (
        <div className="menu" style={{ position: 'absolute', 
        top: anchorEl.getBoundingClientRect().bottom + 'px', 
        left: anchorEl.getBoundingClientRect().left + 'px',
        backgroundColor:'white',
        border: '1px solid' }}>
          {[1, 2, 3, 4, 5].map(l => (
            <div key={l} className="menu-item" onClick={() => handleMenuItemClick(l)}>
              Level {l}
            </div>
          ))}
        </div>
      )}


      <div className="highlighted-sentence" style={{
            backgroundColor:'white', 
            height:'80px',
            marginLeft:'4px',
            marginRight:'4px',
            padding:'5px', 
            borderRadius:'5px', 
            fontWeight:'bold',  
            boxShadow:'1 2px 5px rgba(0,0,0,0.2)', 
            transition:'0.3s', 
            border:'1px solid'}}>
        {/* Render the highlighted sentence here based on the matchedPos */}
        {sentence}
      </div>

    </div>
  );
};

export default HighlightedSentence;
