import React, { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import HighlightedSentence from './HighlightedSentence.jsx'; // Add this import statement
import './style.css';
import SpeechToText from './SpeechToText.tsx'; // Adjust the import path as needed
import SpokenText from './SpokenText.jsx'; // Add this import statement
import AudioPlayer from './AudioPlayer.jsx';
import { findNonOverlappingLCS } from './MyLib.js';

export default function App() {
  const [spokenText, setSpokenText] = useState("know how wowo much money he makes because he didn't want us to have a joint checking account.");

  const receiveSpokenText = (text) => {
    setSpokenText(text);
    console.log("app's text:",text);
  };
  const sentence =
  "I don't know if that was the final straw. It was everything. Julie, I don't know anything about him. I don't know how much money he makes because he didn't want us to have a joint checking account. He always says he's working late at night but he won't give me a number to call other than his cell phone. And that place... it gives me the creeps.";

  const [sentPos,spokenTextPos] = findNonOverlappingLCS(sentence, spokenText); //ordered as the same order of appearance in sentence.
  console.log('spokenTextPos=', spokenTextPos);
  return (
    <div>
      <h1 style={{ textAlign:'center' }}>Hello English Geeks!</h1>
      <div>
        <HighlightedSentence sentence={sentence} matchedPos={sentPos} />
      </div>
      <div>  
        <AudioPlayer audiofile="english_tts_audio.mp3"/> 
      </div>
      <div>
        <SpeechToText sendTextToParent={receiveSpokenText}/>
      </div>
      <div>
        <SpokenText spokenText={spokenText} matchedPos={spokenTextPos}/>
      </div>


    </div>
  );
}
