import React, { useRef } from 'react';

// Define the AudioPlayer functional component
const AudioPlayer = () => {
  // Create a ref for the audio element
  const audioRef = useRef(null);

  // Define the function to play the audio
  const playAudio = () => {
    // Check if the audio element is present
    if (audioRef.current) {
      // Play the audio
      audioRef.current.play();
    }
  };

  return (
    <div>
      {/* Button to trigger the playAudio function */}
      <button onClick={playAudio}>Play Audio</button>
      {/* Audio element with the ref attached */}
      <audio ref={audioRef} src="./english_tts_audio.mp3" />
    </div>
  );
};

export default AudioPlayer;
