//import { findNonOverlappingLCS } from './MyLib.js';


const HighlightedSentence = ({ sentence, matchedPos }) => {
  const wordArray = sentence.split(' ');
  //const nolcs_pos = findNonOverlappingLCS(sentence, text); //ordered as the same order of appearance in sentence.
  //console.log('nolcs=', nolcs_pos);
  const wordlist = wordArray.map((word, i) => {
    //let returnvalue: JSX.Element;
        if (matchedPos[i]) {
      return (
        <p className="sentence" style={{ color: 'green' }} key={i}>
          {word + ' '}
        </p>
      );
    } else {
      return (
        <p className="sentence" key={i}>
          {word + ' '}
        </p>
      );
    }
  });
  return (
    <div>
        <div style={{ marginTop:'10px', marginLeft:'20px', marginRight:'20px',display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}> 
            <button style={{ padding:"1px", width:'30px', fontWeight:'bold', borderBottom:'0px'}}
            > &lt;- </button>
            <button style={{ padding:'1px', width:'30px', fontWeight:'bold', borderBottom:'0px'}}
            > 2 </button>
            <button style={{ padding:'1px', width:'30px', fontWeight:'bold', borderBottom:'0px'}}
            > -&gt; </button>


        </div>

        <div style={{
            backgroundColor:'white', 
            height:'60px',
            marginLeft:'4px',
            marginRight:'4px',
            padding:'5px', 
            borderRadius:'5px', 
            fontWeight:'bold',  
            boxShadow:'1 2px 5px rgba(0,0,0,0.2)', 
            transition:'0.3s', 
            border:'1px solid'}}>
        {wordlist}
        </div>

    </div>
  );
}

export default HighlightedSentence;
