import React, { useState } from 'react';
import './style.css';
import WorkShop from './WorkShop.js';
import { AppBar, Tabs, Tab, Menu, MenuItem, Box } from '@mui/material';

// List of items for the Study menu
const studyItems = [
  'sample1',
  'sample2',
  'sample3',
  // Add more items as needed
];

export default function App() {
  const [material, setMaterial] = useState('sample1');
  const [anchorEl, setAnchorEl] = useState(null);

  const handleOpenMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseMenu = () => {
    setAnchorEl(null);
  };

  const handleMenuItemClick = (item) => {
    setMaterial(item);
    handleCloseMenu();
  };

  return (
    <div>
      <h1 style={{ textAlign:'center' }}>Hello English Geeks!</h1>
      <div>
        <WorkShop material={material} />
      </div>
      <AppBar position="fixed" color="primary" style={{ top: 'auto', bottom: 0 }}>
        <Tabs centered>
          <Tab label="Study" onClick={handleOpenMenu} />
          <Tab label="Guide" />
          <Tab label="About Me" />
        </Tabs>
      </AppBar>
      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleCloseMenu}
      >
        {studyItems.map((item, index) => (
          <MenuItem key={index} onClick={() => handleMenuItemClick(item)}>
            {item}
          </MenuItem>
        ))}
      </Menu>
    </div>
  );
}
