import React, { useState } from 'react';
import './App.css';
import WorkShop from './WorkShop.jsx';

// List of items for the Study menu
const studyItems = [
  'comma-gets-a-cure',
  'alex_and_jordan',
  'the-playground'
  // Add more items as needed
];

export default function App() {
  const [material, setMaterial] = useState(studyItems[0]);
  const [currentTab, setCurrentTab] = useState('Study');
  const [isStudyMenuOpen, setIsStudyMenuOpen] = useState(false);

  const handleMenuItemClick = (item) => {
    setMaterial(item);
    setIsStudyMenuOpen(false);
  };

  const handleTabClick = (tab) => {
    if (tab === 'Study') {
      setIsStudyMenuOpen(!isStudyMenuOpen);
    } else {
      setIsStudyMenuOpen(false);
    }
    setCurrentTab(tab);
  };

  return (
    <div>
      <h2 style={{ textAlign:'center',marginTop:'20px' }}>{material.toUpperCase().replace(/-/g,' ')}</h2>
      <br/>
      <div>
        <WorkShop material={material} />
      </div>
      <div className="tab-container">
        <div
          className={`tab ${currentTab === 'Study' ? 'active' : ''}`}
          onClick={() => handleTabClick('Study')}
        >
          Study
          {isStudyMenuOpen && (
            <div className="dropdown-menu">
              {studyItems.map((item, index) => (
                <div
                  key={index}
                  className="dropdown-item"
                  onClick={() => handleMenuItemClick(item)}
                >
                  {item}
                </div>
              ))}
            </div>
          )}
        </div>
        <div
          className={`tab ${currentTab === 'Guide' ? 'active' : ''}`}
          onClick={() => handleTabClick('Guide')}
        >
          Guidance
        </div>
        <div
          className={`tab ${currentTab === 'About Me' ? 'active' : ''}`}
          onClick={() => handleTabClick('About Me')}
        >
          About Me
        </div>
      </div>
    </div>
  );
}
