import React, { useState, useEffect } from 'react';
import { findNonOverlappingLCS, replaceNumbersWithWords } from './MyLib.js';
import Button from '@mui/material/Button';


const speechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition;
if (!speechRecognition) {
  alert('Speech Recognition API is not supported in this browser');
  //return;
}

const recognition = new speechRecognition();
recognition.continuous = true;
recognition.interimResults = true;
recognition.lang = 'en-US';
//recognition.lang = 'cmn-Hans-CN'; // 设置为普通话中文

//好像没有这个方法 recognition.timeout = 30000;
//recognition.maxAlternatives = 1; //xu

//const wordlist = 'hey';

let rootcounter = 0;
const sentence =
  "I don't know if that was the final straw. It was everything. Julie, I don't know anything about him.  I don't know how much money he  makes because he didn't want us to have a joint checking account. He always says he's working late at night but he won't give me a number to call other than his cell phone. And that place...it gives me the creeps.";

//The effort comes as investigators are still attempting to piece together exactly what happened on March 26, when a wayward cargo ship crashed into one of its support structures, causing six construction workers to plunge to their deaths.';

const wordArray = sentence.split(' ');

const SpeechToText = () => {
  const [isListening, setIsListening] = useState(false);
  const [text, setText] = useState('Key Bridge five');
  //console.log('enter SpeechToText()...');

  const nolcs_pos = findNonOverlappingLCS(sentence, text); //ordered as the same order of appearance in sentence.
  //console.log('nolcs=', nolcs_pos);

  const wordlist = wordArray.map((word, i) => {
    let returnvalue = null;
    if (nolcs_pos[i]) {
      returnvalue = (
        <p className="sentence" style={{ color: 'red' }} key={i}>
          {word + ' '}
        </p>
      );
    } else {
      returnvalue = (
        <p className="sentence" key={i}>
          {word + ' '}
        </p>
      );
    }
    return returnvalue;
  });

  let i = 0;
  useEffect(() => {
    i = i + 1;
    console.log('into useEffect.i=', i);
    recognition.onend = (e) => {
      if (isListening) recognition.start();
    };

    recognition.onresult = (event) => {
      //console.log(event.results.map((result) => result[0]).join(""));
      //console.log('RESULTS：', event.results);
      console.log('最新内容：', event.results[event.results.length - 1][0]);
      let transcript = event.results[event.results.length - 1][0].transcript;
      transcript = replaceNumbersWithWords(transcript);
      setText(transcript);
    };

    isListening ? recognition.start() : recognition.stop();

    return () => recognition.stop();
  }, [isListening]);

  return (
    <div>
      {wordlist}

      <Button variant="contained" color="primary" onClick={() => setIsListening((prevIsListening) => !prevIsListening)}>
      {isListening ? 'Stop Listening' : 'Start Listening'}
      </Button>

      <p>{text}</p>
    </div>
  );
};

export default SpeechToText;
