import React, { useState } from 'react';
import './style.css';

const HighlightedSentence = ({ sentence, matchedPos }) => {
  const [level, setLevel] = useState(3);
  const [previousLevel, setPreviousLevel] = useState(level);
  const [showTranslation, setShowTranslation] = useState(true);

  const handleButtonPress = (newLevel) => {
    setLevel(newLevel);
  };

  const handleFullButtonPress = () => {
      setPreviousLevel(level);
      setLevel(0);
  };

  const handleFullButtonRelease = () => {
    setLevel(previousLevel);
  };

  const handleFullButtonDoubleClick = () => {
    setLevel(0);
  };

  const handleShowTranslationButtonPress = () => {
    setShowTranslation(!showTranslation);
  }
  //console.log("translation",showTranslation);
  const wordArray = sentence.split("||")[level].split(' ');
  //const nolcs_pos = findNonOverlappingLCS(sentence, text); //ordered as the same order of appearance in sentence.
  //console.log('nolcs=', nolcs_pos);
  const wordlist = wordArray.map((word, i) => {
    //let returnvalue: JSX.Element;
        if (matchedPos[i]) {
      return (
        <p className="word" style={{ color: 'green' }} key={i}>
          {word + ' '}
        </p>
      );
    } else {
      return (
        <p className="word" key={i}>
          {word + ' '}
        </p>
      );
    }
  });

  return (
    <div>
      <div className="level-controls" 
      style={{ marginTop:'10px', 
        marginBottom:'5px',
        //marginLeft:'20px', 
        //marginRight:'20px',
        display: 'flex', 
        justifyContent:'center' , 
        alignItems: 'center' }}
      >

        <button
          onMouseDown={handleFullButtonPress}
          //onMouseLeave={handleFullButtonRelease}
          onMouseUp={handleFullButtonRelease}
          onDoubleClick={handleFullButtonDoubleClick}
        >Full
        </button>

        {[1, 2, 3, 4, 5].map((lvl) => (
          <button
            key={lvl}
            onMouseDown={() => handleButtonPress(lvl)}
          >
            Level {lvl}
        </button>
        ))}
        <button
          onMouseDown={handleShowTranslationButtonPress}
        >
          CHN
        </button>
      </div>

      <div className="highlighted-sentence" style={{
            backgroundColor:'white', 
            minHeight:'80px',            
            marginLeft:'4px',
            marginRight:'4px',
            padding:'5px', 
            borderRadius:'5px', 
            fontWeight:'bold',  
            boxShadow:'1 2px 5px rgba(0,0,0,0.2)', 
            transition:'0.3s', 
            border:'1px solid'}}>
        {/* Render the highlighted sentence here based on the matchedPos */}
        <div>{wordlist}</div>
        <div style={{marginTop:'auto',
          position:'relative',
          bottom:0        }}>{showTranslation ? sentence.split("||")[6] : ''}</div>
      </div>

    </div>
  );
};

export default HighlightedSentence;
