import React, { useState } from 'react';
import './style.css';

const HighlightedSentence = ({ sentence, matchedPos }) => {
  const [level, setLevel] = useState(3);
  const [previousLevel, setPreviousLevel] = useState(level);

  const handleButtonPress = (newLevel) => {
    if (newLevel === 0) {
      setPreviousLevel(level);
      setLevel(0);
    } else {
      setLevel(newLevel);
    }
  };

  const handleButtonRelease = () => {
    if (level === 0) {
      setLevel(previousLevel);
    }
  };

  const wordArray = sentence.split("||")[level].split(' ');
  //const nolcs_pos = findNonOverlappingLCS(sentence, text); //ordered as the same order of appearance in sentence.
  //console.log('nolcs=', nolcs_pos);
  const wordlist = wordArray.map((word, i) => {
    //let returnvalue: JSX.Element;
        if (matchedPos[i]) {
      return (
        <p className="word" style={{ color: 'green' }} key={i}>
          {word + ' '}
        </p>
      );
    } else {
      return (
        <p className="word" key={i}>
          {word + ' '}
        </p>
      );
    }
  });

  return (
    <div>
      <div className="level-controls" 
      style={{ marginTop:'10px', 
        //marginLeft:'20px', 
        //marginRight:'20px',
        display: 'flex', 
        justifyContent:'center' , 
        alignItems: 'center' }}
      >
        {[0, 1, 2, 3, 4, 5].map((lvl) => (
          <button
            key={lvl}
            onMouseDown={() => handleButtonPress(lvl)}
            onMouseUp={lvl === 0 ? handleButtonRelease : null}
            onMouseLeave={lvl === 0 ? handleButtonRelease : null}
          >
            Level {lvl}
          </button>
        ))}
      </div>

      <div className="highlighted-sentence" style={{
            backgroundColor:'white', 
            height:'80px',
            marginLeft:'4px',
            marginRight:'4px',
            padding:'5px', 
            borderRadius:'5px', 
            fontWeight:'bold',  
            boxShadow:'1 2px 5px rgba(0,0,0,0.2)', 
            transition:'0.3s', 
            border:'1px solid'}}>
        {/* Render the highlighted sentence here based on the matchedPos */}
        {wordlist}
      </div>

    </div>
  );
};

export default HighlightedSentence;
