import React, { useState } from 'react';
import './style.css';





const HighlightedSentence = ({ sentence, matchedPos }) => {
  const [level, setLevel] = useState(0);
  const [anchorEl, setAnchorEl] = useState(null);

  const handleLevelDecrease = () => {
    setLevel(prevLevel => Math.max(0, prevLevel - 1));
  };

  const handleLevelIncrease = () => {
    setLevel(prevLevel => Math.min(5, prevLevel + 1));
  };

  const handleOpenMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseMenu = () => {
    setAnchorEl(null);
  };

  const handleMenuItemClick = (level) => {
    setLevel(level);
    handleCloseMenu();
  };

  const wordArray = sentence.split("||")[level].split(' ');
  //const nolcs_pos = findNonOverlappingLCS(sentence, text); //ordered as the same order of appearance in sentence.
  //console.log('nolcs=', nolcs_pos);
  const wordlist = wordArray.map((word, i) => {
    //let returnvalue: JSX.Element;
        if (matchedPos[i]) {
      return (
        <p className="word" style={{ color: 'green' }} key={i}>
          {word + ' '}
        </p>
      );
    } else {
      return (
        <p className="word" key={i}>
          {word + ' '}
        </p>
      );
    }
  });



  return (
    <div>
      <div className="level-controls" style={{ marginTop:'10px', marginLeft:'20px', marginRight:'20px',display: 'flex', justifyContent:'center' , alignItems: 'center' }}>
        <button onClick={handleLevelDecrease}>Easier</button>
        <button onClick={handleOpenMenu}>Level {level}</button>
        <button onClick={handleLevelIncrease}>Harder</button>
      </div>
      {anchorEl && (
        <div className="menu" style={{ position: 'absolute', 
        top: anchorEl.getBoundingClientRect().bottom + 'px', 
        left: anchorEl.getBoundingClientRect().left + 'px',
        backgroundColor:'white',
        }}>
          {[0, 1, 2, 3, 4, 5].map(l => (
            <div key={l} className="menu-item" onClick={() => handleMenuItemClick(l)}
            style = {{border: '1px solid', padding: '5px', cursor: 'pointer', width: '47px', fontSize:'13px',  textAlign: 'center',height: '11px'}}
            >
              Level {l}
            </div>
          ))}
        </div>
      )}


      <div className="highlighted-sentence" style={{
            backgroundColor:'white', 
            height:'80px',
            marginLeft:'4px',
            marginRight:'4px',
            padding:'5px', 
            borderRadius:'5px', 
            fontWeight:'bold',  
            boxShadow:'1 2px 5px rgba(0,0,0,0.2)', 
            transition:'0.3s', 
            border:'1px solid'}}>
        {/* Render the highlighted sentence here based on the matchedPos */}
        {wordlist}
      </div>

    </div>
  );
};

export default HighlightedSentence;
